    if (filtered.length > 0) {
      setState({
        ...state,
        error: "Two candidates cannot be in thesame party",
        showError: true,
      });
      return;
    }

    const NewCandidate = {
      ...newCandidate,
      name: newCandidate.name
        .toLowerCase()
        .replace(/\b\w/g, (s) => s.toUpperCase()),
      role: voteToRender.value.role,
      votes: 0,
      voteId: voteId,
    };

    // Upload the data to socialDb
    console.log(NewCandidate);
    // Social.set({
    //   index: {
    //     voteChainTest: JSON.stringify({
    //       key: "candidate",
    //       value: NewCandidate,
    //     }),
    //   },
    // });
    // setNewCandidate({
    //   name: "",
    //   party: "",
    //   role: "",
    //   votes: 0,
    // });
    setState({
      ...state,
      error: "",
      showError: false,
    });
    return {
      index: {
        voteChainTest: JSON.stringify({
          key: "candidate",
          value: NewCandidate,
        }),
      },
    };
  } else {
    setState({
      ...state,
      error: `Candidate's ${
        newCandidate.name === "" ? "Name" : "Party"
      } can not be empty`,
      showError: true,
    });
    return null;
  }
}

const secText = styled.h3`
  text-align: center;
`;

// Only signed In users can access the page
return (
  <>
    {accountId ? (
      <Widget
        src="abnakore.near/widget/Wrapper"
        props={{
          body: (
            <div className="main-body">
              {voteToRender ? (
                <div className="two-sides">
                  <Widget
                    src="abnakore.near/widget/Aside"
                    props={{
                      objs: pages,
                      active: `/abnakore.near/widget/ManageCandidates?vote=${voteToRender.blockHeight}`,
                    }}
                  />
                  {voteToRender.value.creator === accountId ? (
                    <div className="body-contents">
                      <h1>Manage Candidates</h1>
                      <Widget
                        src="abnakore.near/widget/Table"
                        props={{
                          headings: [
                            "S/N",
                            "Candidate's Name",
                            "Party",
                            "Role",
                          ],
                          data: Object.values(
                            voteToRender.value.candidates
                              .sort((a, b) => a.name > b.name)
                              .map((c, i) =>
                                [i + 1].concat([c.name, c.party, c.role])
                              )
                          ),
                        }}
                      />
                      <div className="form">
                        <secText>Add Candidate</secText>
                        {state.showError && (
                          <p style={{ color: "red", textAlign: "center" }}>
                            {state.error}
                          </p>
                        )}
                        <div className="flex">
                          <Widget
                            src="abnakore.near/widget/Input.jsx"
                            props={{
                              type: "text",
                              placeholder: "Full Name",
                              required: true,
                              item: "name",
                              items: newCandidate,
                              setItem: setNewCandidate,
                              otherAttributes: {
                                value: newCandidate.name,
                              },
                            }}
                          />

                          <select
                            className="drop-down"
                            value={newCandidate.party}
                            onChange={updateDropDown}
                            name="party"
                            required
                          >
                            <option className="option" value="">
                              Select Party
                            </option>
                            {getUnusedParties().map((party) => (
                              <option
                                className="option"
                                key={party.acronym}
                                value={party.acronym}
                              >
                                {party.name} ({party.acronym})
                              </option>
                            ))}
                          </select>
                        </div>
                        <CommitButton data={save} onCommit={refresh}>
                          Add
                        </CommitButton>
                      </div>
                    </div>
                  ) : (
                    <div className="body-contents">
                      <h1>You don't have access to this page</h1>
                      <Link to="/abnakore.near/widget/VoteChain">
                        Back to Home Page
                      </Link>
                    </div>
                  )}
                </div>
              ) : (
                <div className="body-contents">
                  <h1>Vote Doesn't exist</h1>
                </div>
              )}
            </div>
          ),
        }}
      />
    ) : (
      <Widget src="abnakore.near/widget/SignIn.jsx" props={props} />
    )}
  </>
);
